//import axios from "axios";
import React, { useState, useRef } from "react";
import { Link } from "react-router-dom";
import Style from './login.module.css';

function Login() {
  const [inputId, setInputId] = useState("");
  const [inputPw, setInputPw] = useState("");
  const IdInput = useRef();
  const PwInput = useRef();
  //input data의 변화 있을 때마다 value값 변경하여 useState
  const handleInputId = (e) => {
    setInputId(e.target.value);
  };

  const handleInputPw = (e) => {
    setInputPw(e.target.value);
  };

  //Login 클릭
  const submitHandler = () => {
    console.log("login");
    if (inputId.length < 5) {
      IdInput.current.focus();
      return;
    }
    if (inputPw.length < 5) {
      PwInput.current.focus();
      return;
    }
  };

  return (
    <div className={Style.cover}>
      <div className={Style.titleCover}>
        <h2 className={Style.title}>Health-Info</h2>
      </div>
      <div className={Style.formCover}>
        <form className={Style.loginform}>
          <p>ID</p>
          <input
            className="form-control shadow-lg"
            ref={IdInput}
            type="text"
            name="input_id"
            value={inputId}
            placeholder="Example@mail.com"
            onChange={handleInputId}
          />
          <p>PW</p>
          <input
            className="form-control shadow-lg"
            ref={PwInput}
            type="text"
            name="input_pw"
            value={inputPw}
            placeholder="Password"
            onChange={handleInputPw}
          />
        </form>
      </div>
      <div className={Style.buttonCover}>
        <div className={Style.loginBtn}>
          <button type="button" className="btn btn-info mx-5 shadow-lg border border-dark" onClick={submitHandler}>Login</button>
        </div>
        <div className={Style.signupBtn}>
          <Link to="/signup"><button type="button" className="btn btn-info translate-middle-y mx-5 shadow-lg border border-dark">Sign up</button></Link>
        </div>
      </div>
    </div>
  );
}

export default Login;
